    private void btnProcesarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProcesarActionPerformed
        switch (cbOpcion.getSelectedIndex()) {
            case 0: ingresar(); break;
            case 1: consultar(); break;
            case 2: modificar(); break;
            default: eliminar();
        }
    }//GEN-LAST:event_btnProcesarActionPerformed

    private void btnBorrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarActionPerformed
        txtCodigo.setText("");
        txtDescripcion.setText("");
        txtPrecio.setText("");
        txtStock.setText("");
        txtS.setText("");
        cbOpcion.setSelectedIndex(0);
    }//GEN-LAST:event_btnBorrarActionPerformed

    private void cbOpcionItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cbOpcionItemStateChanged
        int opcion = cbOpcion.getSelectedIndex();
        boolean visible = true;
        if(opcion == 1 || opcion == 3){
            visible = false;
        }
        lblDescripcion.setVisible(visible);
        txtDescripcion.setVisible(visible);
        lblPrecio.setVisible(visible);
        txtPrecio.setVisible(visible);
        lblStock.setVisible(visible);
        txtStock.setVisible(visible);
    }//GEN-LAST:event_cbOpcionItemStateChanged

    // <editor-fold defaultstate="collapsed" desc="Gets de controles">  
    private int getCodigo(){
        return Integer.parseInt(txtCodigo.getText());
    }
    private String getDescripcion(){
        return txtDescripcion.getText();
    }
    private double getPrecio(){
        return Double.parseDouble(txtPrecio.getText());
    }
    private int getStock(){
        return Integer.parseInt(txtStock.getText());
    }
