    // <editor-fold defaultstate="collapsed" desc="Metodos genericos">
    private void imprimir(String s){
        txtS.append(s + "\n");
    }
    public String rellena(String cad){
        int longitud = cad.length();
        for (int i = longitud; i < 15; i++) {
            cad += " ";
        }
        return cad;
    }
    public void listar(){
        if(p.tamanio()>0){
            txtS.setText("");
            DefaultTableModel df = ((DefaultTableModel) tbProductos.getModel());
            df.setRowCount(0);
            for (Producto pro : p.getProductos()) {
                df.addRow(new Object[] { 
                    pro.getCodigo(),
                    pro.getDescripcion(),
                    pro.getPrecio(),
                    pro.getStock()
                });
            }
            imprimir("Total de productos: " + p.tamanio() );
            imprimir("Producto mas caro: " + p.mayorPrecio() );
            imprimir("Producto mas barato: " + p.menorPrecio() );
        }
        else
        {
            imprimir("No hay productos.");
        }
    }
    public void mensaje(String mensaje, String titulo, int tipo){
        JOptionPane.showMessageDialog(this, mensaje, titulo, tipo);
    }
    public void mensajeError(String mensaje){
        mensaje(mensaje, "Mensaje de error!", JOptionPane.ERROR_MESSAGE);
    }
    // </editor-fold>
    
    public void ingresar(){
        Producto prod = p.buscar(getCodigo());
        if(prod == null){
            prod = new Producto(getCodigo(), getStock(), getDescripcion(), getPrecio());
            p.adicionar(prod);
            listar();
            mensaje("Producto ingresado.", "Mensaje de confirmación", 
                        JOptionPane.INFORMATION_MESSAGE);
        }else{
            mensajeError("Código de producto ya existe");
        }
    }
    
    public void consultar()
    {
        txtS.setText("");
        Producto prod = p.buscar(getCodigo());
        if(prod != null){
            imprimir("Código: \t" + prod.getCodigo());
            imprimir("Descripción: \t" + prod.getDescripcion());
            imprimir("Precio Unit: \t" + prod.getPrecio());
            imprimir("Stock: \t" + prod.getStock());
        }else{
            mensajeError("Producto no existe");
        }
    }
    
    public void modificar(){
        Producto prod = p.buscar(getCodigo());
        if(prod != null){
            prod.setDescripcion(getDescripcion());
            prod.setPrecio(getPrecio());
            prod.setStock(getStock());
            p.SaveData();
            listar();
            mensaje("Producto fue modificado.", "Mensaje de confirmación", 
                        JOptionPane.INFORMATION_MESSAGE);
        }else{
            mensajeError("Producto no existe");
        }
    }
    
    public void eliminar(){
        Producto prod = p.buscar(getCodigo());
        if(prod != null){
            p.eliminar(prod);
            listar();
            mensaje("Producto fue eliminado.", "Mensaje de confirmación", 
                        JOptionPane.INFORMATION_MESSAGE);            
        }else{
            mensajeError("Producto no existe");
        }
    }
    
